import React from "react";
import "./styles.css";
import LeftSection from "./LeftSection";
import RightSection from "./RightSection";

import { getData } from "./eventsData";

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      eventsData: [],
      eventsDataArr :[]
    };
  }

  async componentDidMount() {
    const respData = await getData();
    this.setState({ eventsData: respData });
    console.log(this.state.eventsData.events[0].start);
    const pnk = new Date(this.state.eventsData.events[0].start);
    console.log(this.state.eventsData.events.length);
    const dateArr =[];
    var k=0;
     for(let i =0;i<this.state.eventsData.events.length;i++)
     {
        dateArr[k++] =  this.state.eventsData.events[i].start;
        dateArr[k++] =  this.state.eventsData.events[i].end;
     }
     console.log(dateArr);
     this.setState({ eventsDataArr: dateArr });
     console.log(this.state);
  }


  render() {
    const { eventsData } = this.state;
    const { eventsDataArr } = this.state;


    return (
      <div className="container">
        <LeftSection eventsData={eventsData} />
        <RightSection eventsDataArr={eventsDataArr} />
      </div>
    );
  }
}

export default App;
